/**
  PlaylistPt2.js
  @author Ethan Mil
  @version 1.0.0

  A React component that defines a playlist that shuffles on page reload.
*/

import React, { useEffect, useState } from 'react';
import Song from './Song';
import Podcast from './Podcast';
import '../App.css';

const PlaylistPt2 = () => {
  const [audioTracks, setAudioTracks] = useState([]);

  useEffect(() => {
    const getSongs = () => {
      const xhr = new XMLHttpRequest();
      xhr.open("GET", "http://localhost:3001/tracks");
      xhr.send();
      xhr.responseType = "json";
      xhr.onload = () => {
        if (xhr.readyState == 4 && xhr.status == 200) {
          const data = xhr.response;
          setAudioTracks(data.tracks);
        } else {
          console.log(`Error: ${xhr.status}`);
        }
      };
    };

    getSongs();
  }, []);

  return (
    <div>
      <h2>Playlist</h2>
      <div className="shuffled-playlist">
        {audioTracks && audioTracks.map((item, index) => (
          <div key={index} className="insert" style={{ padding: '1px' }}>
            {item.title && item.artist && item.year && (
              <Song title={item.title} artist={item.artist} year={item.year} />
            )}
            {item.episode && item.episodeTitle && (
              <Podcast
                episode={item.episode}
                episodeTitle={item.episodeTitle}
                season={item.season}
              />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default PlaylistPt2;
