/**
  Playlist.js
  @author Ethan Mil
  @version 1.0.0

  A React component that defines a playlist.
*/

import React, { useEffect, useState } from 'react';
import Song from './Song';
import Podcast from './Podcast';

const Playlist = () => {

  const songInfo = [
    { title: 'These Girls', artist: 'Sticky Fingers', year: 2013 },
    { title: 'Virginia Beach', artist: 'Drake', year: 2023 },
    { title: 'Carry On', artist: 'GRiZ', year: 2022 },
  ];

  const podcastInfo = [
    { episode: 352, episodeTitle: 'Fishing in the Ocean' },
    { episode: 23, episodeTitle: 'Muscle Hypertrophy Training' },
    { season: 2, episode: 2, episodeTitle: 'How to Build a House' },
  ];

    //combine
    const combinedPlaylist = [...songInfo, ...podcastInfo];

    const [shuffledPlaylist, setShuffledPlaylist] = useState([]);

    // Set the shuffled playlist to state
    const shufflePlaylist = () => {
      const shuffledList = [...combinedList];
      for (let i = shuffledList.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [shuffledList[i], shuffledList[j]] = [shuffledList[j], shuffledList[i]];
      }
      setShuffledPlaylist(shuffledList);
    };

    useEffect(() => {
      shufflePlaylist();
    }, []);

    return (
      <div>
          <h2> Shuffled Playlist</h2>
          <div className="shuffled-playlist">
          {/* Your shuffled playlist components */}
          {shuffledPlaylist.map((item, index) => (
              <div key={index} className="entry">
              {item.hasOwnProperty('artist') ? (
                  <Song {...item} />
              ) : (
                  <Podcast {...item} />
              )}
              </div>
          ))}
          </div>
      </div>
    );
  };

  export default Playlist;
